import { Column, Id } from "../types";
import TrashIcon from '../icons/TrashIcon';

interface Props {
    column: Column;
    deleteColumn: (id: Id) => void;
}

export default function ColumnContainer(props:Props) {
    const {column, deleteColumn} = props;

    return (
        <div className="bg-gray-900 w-[350px] h-[500px] max-h-[500px] rounded-md flex flex-col">
            {/* Column title */}
            <div className="bg-black text-md h-[60px] cursor-grab rounded-md rounded-b-none p-3 font-bold border-gray-900 border-4 flex items-center justify-between">
                <div className="flex gap-2">
                    <div className="flex justify-center items-center bg-gray-900 px-2 py-1 text-sm rounded-full">
                        0
                    </div>
                    {column.title}
                </div>
                <button
                    onClick={() => deleteColumn(column.id)} 
                    className="stroke-gray-500 hover:stroke-white hover:bg-gray-900 rounded-full px-1 py-2">
                    <TrashIcon/>
                </button>
            </div>
            {/* Column task container */}
            <div className="flex flex-grow">
                content
            </div>
            {/* Column footer */}
            <div>Footer</div>
        </div>
    );
}
